    private void entrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_entrarActionPerformed
        String nomeconta = Jtconta.getText();
        String senha = Jtsenha.getText();
        boolean login = false;
        
            for (int i= 0; i < Contas.lista.size(); i++){
                if (Contas.lista.get(i).getConta().equals(nomeconta) && Contas.lista.get(i).getSenha().equals(senha)){
                    login = true;
                    MenuConta men = new MenuConta();
                    men.nconta = i;
                    men.conta = Contas.lista.get(i).getConta();
                    men.setVisible(true);
                    dispose();
                }
            }
            if (!login){
                    JOptionPane.showMessageDialog(null, "Conta ou senha invÃ¡lidos.");
                    Jtconta.setText(null);
                    Jtsenha.setText(null);
            }
    }//GEN-LAST:event_entrarActionPerformed

